//**************************************************************************************************
//! [Description of PYRAMID_ACCEL_CALIBRATION_FunctionZero]
//!
//! \note       None
//!
//! \param[in]  parameterZero - [description of parameterZero]
//!
//! \return     [Description of return value]
//**************************************************************************************************
const char* PYRAMID_ACCEL_CALIB_ToStr(const PYRAMID_ACCEL_CALIB_HANDLE* const Node)
{
    static char text[250] = {0};
    if (Node)
    {
        strcpy(text, "");
        snprintf(text, sizeof(text), "%sN:%u," CRLF, text, Node->num);
        snprintf(text, sizeof(text), "%sA1:%s," CRLF, text, VECTOR_fToStr(&Node->A1));
        snprintf(text, sizeof(text), "%sA2:%s," CRLF, text, VECTOR_fToStr(&Node->A2));
        snprintf(text, sizeof(text), "%sScale:%s," CRLF, text, VECTOR_fToStr(&Node->Scale));
        snprintf(text, sizeof(text), "%sOffset:%s," CRLF, text, VECTOR_fToStr(&Node->Offset));
    }
    return text;
} // end of MODULE_FunctionZero()

