//**************************************************************************************************
//! calculate the length of a 3D vector
//!
//! \note       Calculations are made according to the Pythagorean theorem for 3D
//!
//! \param[in]  Node - vector node
//!
//! \return     3D vector length
//**************************************************************************************************
FLOAT32 VECTOR_CalcNormF(const VECTOR_F* const Node)
{
    FLOAT32 length = 0.0;
    if (NULL_PTR != Node)
    {
        FLOAT32 argument = 0.0;
        argument += ((Node->dx) * (Node->dx));
        argument += ((Node->dy) * (Node->dy));
        argument += ((Node->dz) * (Node->dz));
        length = sqrtf(argument);
    } // end of if (Node)
    return length;
} // end of VECTOR_CalcNormF()

