#define VECTOR_EPSILONT (0.00000000001)

//! indices of the projection axes of vector in an array when writing a vector as an array
typedef enum {
    // X axis index
    VECTOR_AXIS_X = 0,
    // Y axis index
    VECTOR_AXIS_Y = 1,
    // Z axis index
    VECTOR_AXIS_Z = 2,
    // undef axis index
    VECTOR_AXIS_UNDEF = 3,
}VECTOR_AXIS_INDEX;



